create table location (id bigint generated by default as identity, name varchar(255), primary key (id))
create table sample (id bigint generated by default as identity, date_collected date, depth float(53) not null, shear_strength float(53) not null, unit_weight float(53) not null, water_content float(53) not null, location_id bigint, primary key (id))
alter table if exists sample add constraint location_fk foreign key (location_id) references location